import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-card',
  template: `
    <div class="card">
      <img [src]="imageUrl" class="card-img-top" [alt]="title" />
      <div class="card-body">
        <h5 class="card-title">{{ title }}</h5>
        <p class="card-text">{{ description }}</p>
        <!-- -->
        <div class="chip" *ngIf="release_date > today"></div>
      </div>
    </div>
  `,
  styleUrls: ['./card.component.css'],
})
export class CardComponent {
  @Input() imageUrl!: string;
  @Input() title!: string;
  @Input() description!: string;
  @Input() release_date!: Date;

  constructor() {}

  ngOnInit(): void {
    console.log(this.release_date);
  }

  // Get current date
  get today() {
    return new Date();
  }

  // Get chip
  get chip() {
    if (this.release_date > this.today) {
      return 'Upcoming';
    } 
    else if (this.release_date < this.today && this.release_date > new Date(this.today.getTime() - 30 * 24 * 60 * 60 * 1000)) {
      return 'New';
    }
    else {
      return '';
    }
  }

  get showChip() {
    return this.chip !== '';
  }
}
