import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-card',
  template: `
    <div class="card">
      <img [src]="imageUrl" class="card-img-top" [alt]="title" />
      <div class="card-body">
        <h5 class="card-title">{{ title }}</h5>
        <p class="card-text">{{ description }}</p>
        <span class="badge text-bg-primary" *ngIf="showChip">{{ chip }}</span>
        <a href="#" class="btn btn-primary">{{ getStatus }}</a>
      </div>
    </div>
  `,
  styleUrls: ['./card.component.css'],
})
export class CardComponent {
  @Input() imageUrl!: string;
  @Input() title!: string;
  @Input() description!: string;
  @Input() status!: string;
  @Input() release_date!: Date;

  constructor() {}

  ngOnInit(): void {
  }

  // Get current date
  get today() {
    return new Date();
  }

  // Get chip
  get chip() {
    console.log(this.release_date + ' ' + this.today);
    if (this.release_date > this.today) {
      return 'Upcoming';
    } else if (
      this.release_date < this.today &&
      this.release_date >
        new Date(this.today.getTime() - 30 * 24 * 60 * 60 * 1000)
    ) {
      return 'New';
    } else {
      return '';
    }
  }

  get showChip() {
    return this.chip !== '';
  }

  get getStatus() {
    switch (this.status) {
      case 'Coming Soon':
        return 'Coming Soon';
      case 'Released':
        return 'Get Now';
      case 'Early Access':
        return 'Get Now';
      case 'Cancelled':
        return 'Cancelled';
      case 'Beta':
        return 'Get Now';
      case 'Alpha':
        return 'Get Now';
      case 'Demo':
        return 'Get Now';
      default:
        return 'Coming Soon';
    }
  }
}
